package may19;

public class ArithmeticOperationsEX {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int A =200;
	int B= 100;
	int Sum = A+B;
	int Sub =A-B;
	int Mul =A*B;
	int Div =A/B;
	System.out.println("The Sum of A and B= " + Sum);
	
	
	System.out.println("The Sub of A and B= "  + Sub);
	
	System.out.println("The Mul of A and B= " + Mul);
	
	System.out.println("The Div of A and B= " + Div);
	}

}
