package may19;

public class PostfixEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int A =10;
System.out.println("The value of A" + A);
A++;
System.out.println("The value of A" + A);

System.out.println("The value of A" + A++);

System.out.println("The value of A" + A);

int B=20;
System.out.println("The value of B" + B);
B++;
System.out.println("The value of B" + B);

System.out.println("The value of B" + B++);

System.out.println("The value of B" + B);

}

}
